import { Component } from '@angular/core';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Post } from './post';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  readonly ROOT_URL_= 'https://jsonplaceholder.typicode.com';

  // 1
  // posts: any
  // 2
  // posts: Observable<Post[]> = new Observable<Post[]>;
  // 3, 4
  posts: Observable<any> = new Observable<any>;

  constructor(private http: HttpClient) {}

  getPosts() {

    // 1
    // this.posts = this.http.get(this.ROOT_URL_ + '/posts');

    // 2
    // this.posts = this.http.get(this.ROOT_URL_+ '/posts')

    // 4 Http Params
    // let params = new HttpParams().set('userId', '1');
    // this.posts = this.http.get(this.ROOT_URL_ + '/posts', { params })

    // 5 Http Headers
    let headers = new HttpHeaders().set('Authorization', 'auth-token');
    this.posts = this.http.get(this.ROOT_URL_ + '/posts', { headers });



  }

}
<h1>Angular HTTP Basic</h1>

<button (click)="getPosts()">Get Posts</button>

<div *ngFor="let post of posts | async">
  {{ post | json }}
</div>
