import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.sass']
})
export class AppComponent {
  // FormGroup Schreibmöglichkeit (eher für einfache Formulare)
  public loginForm: FormGroup = new FormGroup({
    username: new FormControl('', [
      Validators.required,
      Validators.minLength(5)
    ], []),
    password: new FormControl('', [
      Validators.required
    ], [])
  });
  constructor() {
    this.loginForm.valueChanges.subscribe(console.log);
  }
}


  // FormBuilder Schreibmöglichkeit (eher für komplexe/dynamische Formulare)

//   public loginFormFB: FormGroup;

//   constructor(private _fb: FormBuilder) {
//     this.loginFormFB = this._fb.group({
//       username: ['', [
//         Validators.required,
//         Validators.minLength(5)
//       ], []],
//       password: ['', [
//         Validators.required
//       ], []]
//     });
//   }
// }
<p>{{ loginForm.value | json }}</p>

<form [formGroup]="loginForm">

  <input formControlName="username" type="text">
  <ng-container *ngIf="loginForm.controls['username'] as username">

  <ng-container *ngIf="username.dirty || username.touched">

    <ng-container *ngIf="username.errors as errors">

    <p class="error" *ngIf="errors.required">Es muss ein Username eingegeben werden.
    </p>

    <p class="error" *ngIf="errors.minlength">Es sollten mindestens {{ errors.minlength.requiredLength }} Buchstaben geben.
    </p>
    </ng-container>
  </ng-container>
</ng-container>

  <input formControlName="password" type="password">

  <button [disabled]="!(loginForm.valid && (loginForm.dirty || loginForm.touched))" type="submit">Senden</button>

</form>
